mapper.update:
update board
set del_yn=0
where iboard=#{iboard}

mapper.select:
SELECT iboard,pic
FROM board_image
WHERE iboard = #{iboard}

mapper.update:
update board
set title = #{title}
,ctnt = #{ctnt}
where iboard =#{iboard} and iuser =#{iuser}

UPDATE board
SET board_view = board_view+1
WHERE iboard = #{iboard}
