mapper.update:
update board
set title = #{title}
,icategory =#{icategory}
,ctnt = #{ctnt}
,iuser = #{iuser}
where iboard =#{iboard} and iuser=#{iuser}

update board
set del_yn=0
where iuser=#{iuser} and iboard=#{iboard}

mapper.select:
SELECT iboard,pic
FROM board_image
WHERE iboard = #{iboard}

mapper.update:
update board
set title = #{title}
,ctnt = #{ctnt}
