mapper.select:
SELECT IfNULL(max(del_yn),0) del_yn
FROM review
WHERE iuser = #{iuser} AND iorder = #{iorder} AND iitem = #{iitem}

mapper.update:
UPDATE review
SET review_ctnt = #{reviewCtnt},
star_rating = #{starRating}
WHERE ireview = #{ireview} AND iuser = #{iuser}

UPDATE review
SET pic = #{pic}
WHERE ireview = #{ireview}

UPDATE review
SET del_yn = 0
WHERE ireview = #{ireview} AND iuser = #{iuser}
