mapper.insert:
INSERT INTO user
SET user_id = #{uid}
, password = #{upw}
, email = #{email}
, name = #{name}
, birth_date = #{birth_date}
, phone = #{phone}
, user_address = #{user_address}
, role = #{role}

mapper.select:
SELECT iuser, user_id uid, password upw, name, role
FROM user
WHERE user_id = #{uid}

SELECT iuser, ip, access_token accessToken, refresh_token refreshToken, created_at createdAt, updated_at updatedAt
FROM user_token
WHERE iuser = #{iuser} AND ip = #{ip}

mapper.update:
UPDATE user
SET del_yn = 0
where iuser = #{iuser}

UPDATE user
SET password = #{upw}
, email = #{email}
, name = #{name}
, birth_date = #{birth_date}
, phone = #{phone}
, user_address = #{user_address}
where user_id = #{uid}
